import React, { useEffect, useState } from 'react'
import { collection, addDoc, onSnapshot } from "firebase/firestore";
import './reservas.css'
import db from '../../config/dbFirebase';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Table } from 'react-bootstrap';

function Reservas() {
  //para guardar el listado de reservas
  const[reservas, setReservas] = useState([]);
  //este guarda los valores del formulario
  const [formData, setFormData] = useState({
    nombre: '',
    telefono: '',
    fecha: '',
    hora: '',
    comensales: '',
    email: ''
  })

  const onSave = (event) =>{
    event.preventDefault();
    console.log(formData);
    addDoc(collection(db,'reserva'), formData);
    alert('Se guardo con exito!')

  }

  const onChange = (e) =>{
      setFormData({
        ...formData, [e.target.name]: e.target.value
      })
  }

  const getReservas = async() =>{
    onSnapshot(collection(db, 'reserva'), (snapshot) =>{
      const lista = [];
      snapshot.forEach(doc => lista.push({...doc.data(), id: doc.id}))
      setReservas(lista)
    })
    console.log(reservas);
  }

 useEffect(() =>{
getReservas();
 },[])

  return (
    <>
    <div >

   <div>
    <Table>
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Telefono</th>
          <th>Fecha</th>
          <th>Hora</th>
          <th>Comensales</th>
        </tr>
      </thead>
      <tbody>
        {
          reservas.map(reserva => {
            return(
              <tr>
                <td>{reserva.nombre}</td>
                <td>{reserva.telefono}</td>
                <td>{reserva.fecha}</td>
                <td>{reserva.hora}</td>
                <td>{reserva.comensales}</td>
              </tr>
            )
          })
        }
      </tbody>
    </Table>
   </div>

    </div>
    </>
  )
}

export default Reservas
