import React, { useEffect, useState } from 'react'
import './singin.css'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
//import Singin from './Singin';

function Singin() {

  const [formData, setFormData] = useState({
    nombre: '',
    email: ''
  })
  const onChange = (e) =>{
    setFormData({
      ...formData, [e.target.name]: e.target.value
    })
    console.log(formData);
}
async function onSave(event){
  event.preventDefault ()
  await fetch("http://localhost:3002/login", {
    method: "POST",
    headers: {
        "Content-Type": "application/json"
    },
    body: JSON.stringify(formData)
}).then(resp => resp.json() ).then(result =>  console.log(result))

  alert('Felicitaciones, te haz logeado con éxito!')
}

useEffect(() =>{
getReservas();
},[])

return
(
  <>
  <div >
  <div className='form-contenedor2'>
 <Form onSubmit={onSave} className='form-singin'>
    <Form.Group className="mb-3" controlId="formBasicEmail">


      <Form.Label>Email</Form.Label>
      <Form.Control type="email" name='email' value={Form.email} onChange={onChange} placeholder="Escribe tu email" />
      <Form.Label>Password</Form.Label>
      <Form.Control type="string" value={Form.password} onChange={onChange} name='password' placeholder="Escribe tu password" />
      <Form.Text className="text-muted">
       Tu informacion es privada y no sera compartida o reutilizada.
      </Form.Text>
    </Form.Group>

    <Button variant="primary" type="submit">
      Aceptar
    </Button>
  </Form>
 </div>


  </div>
  </>
)
  }




export default Singin
